import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timerCounter: 0,
      timerSplitData: [],
      isPaused: true
    }
    this.splitTime = this.splitTime.bind(this);
    this.toggleTimer = this.toggleTimer.bind(this);
  }

  componentDidMount() {
    this.stopWatch = setInterval(() => {
      if (this.state.isPaused) {
        return null;
      } else {
        this.setState({ timerCounter: this.state.timerCounter + 1 });
      }
    }, 1);
  }

  // componentWillUnmount() {
  //   clearInterval(this.stopWatch);
  // }

  convertMillisecond(ms) {
    let seconds = ms / 1000;
    let minutes = seconds / 60;

    return `${parseInt(minutes)} : ${parseInt(seconds % 60)}.${m%100}`
  }

  splitTime() {
    this.setState({
      timerSplitData: [this.state.timerCounter, ...this.state.timerSplitData]
    });
  }

  toggleTimer() {
    this.setState({ isPaused: !this.state.isPaused });
  }

  render() {
    const { timerCounter, timerSplitData, isPaused } = this.state;
    return (
      <div className="App">
        <h1>Stop Timer Application</h1>

        <div style={{ display: "flex", alignItems: "center" }}>
          <p>{this.convertMillisecond(timerCounter)}</p>
          <button
            onClick={this.splitTime}
          >split</button>

          <button
            onClick={this.toggleTimer}
          >{isPaused ? "play" : "pause"}</button>
        </div>

        {timerSplitData.map((timer, index) => {
          return <p
            key={index.toString()}
          >
            {this.convertMillisecond(timer)}
          </p>
        })}
      </div>
    );
  }
}

export default App;
